SELECT * , (SELECT COUNT(user_id) from test_done WHERE test_id = '3ba74f82-ee3d-4cf8-8b7f-fde2b5103175') as allcount ,(SELECT AVG(points_scored) from test_done WHERE test_id = '3ba74f82-ee3d-4cf8-8b7f-fde2b5103175') as avgscore,(SELECT AVG(points_total) from test_done WHERE test_id = '3ba74f82-ee3d-4cf8-8b7f-fde2b5103175') as avgtotal FROM test_done as Stats INNER JOIN users U ON U.user_Id = Stats.user_id WHERE test_id = '3ba74f82-ee3d-4cf8-8b7f-fde2b5103175' ORDER BY points_scored DESC, finished_at ASC